insert into "books" ("average_rating", "buy_link", "description", "large_image_url", "list_price", "page_count", "published_at", "ratings_count", "thumbnail_image_url", "title") values (DEFAULT, DEFAULT, 'The book explores the notion of soft power as set of theoretical arguments about power, and as a reflection of how each country perceives what is an increasingly necessary perspective on international relations in an age of ubiquitous global communication flows and encroaching networks of non-state actors. Soft power is discussed a means by which public diplomacy is justified and in the process, reflects arguments for how each state sees what is possible through soft power.', DEFAULT, DEFAULT, '300', '2012', DEFAULT, 'http://books.google.com/books/content?id=vPYTvqf6fzsC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'The Rhetoric of Soft Power')ON CONFLICT DO NOTHING;
