insert into "books" ("average_rating", "buy_link", "description", "large_image_url", "list_price", "page_count", "published_at", "ratings_count", "thumbnail_image_url", "title") values (DEFAULT, DEFAULT, 'The result of more than ten years of research, A Dictionary of Creek/Muskogee draws on the expertise of a linguist and a native Creek speaker to yield the first modern dictionary of the Creek language of the southeastern United States. The dictionaryøcontains over seven thousand Creek-English entries, over four thousand English-Creek entries, and over four hundred Creek place names in Alabama, Georgia, Florida, and Oklahoma. The volume also includes illustrations, a map, antonyms, dialects, stylistic information, word histories, and other useful reference material. Entries are given in both the traditional Creek spelling and a modern phonemic transcription. A Dictionary of Creek/Muskogee is the standard reference work for the Creek language.', DEFAULT, DEFAULT, '353', '2000', DEFAULT, 'http://books.google.com/books/content?id=7iJfMERhC1EC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'A Dictionary of Creek/Muskogee')ON CONFLICT DO NOTHING;
