insert into "books" ("average_rating", "buy_link", "description", "large_image_url", "list_price", "page_count", "published_at", "ratings_count", "thumbnail_image_url", "title") values ('4.5', DEFAULT, 'All of science springs from the observation of nature. In this classic book, the late Professor Minnaert accompanies the reader on a tour of nature''s light and color and reveals the myriad phenomena that may be observed outdoors with no more than a pair of eyes and an enquiring mind. From the intriguing shape of the dapples beneath a tree on a sunny day, via rainbows, mirages, and haloes, the colors of liquid, ice, and the sky, to the appearance of the sun, moon, planets, and stars - Minnaert describes and explains them all in a clear language accessible to laymen. This new English edition is supplemented by 80 plates, over half of them in color, taken by the acclaimed photographer Pekka Parviainen, illustrating many of the phenomena - ordinary and exotic - discussed in the book.', DEFAULT, DEFAULT, '417', '1995-04-13', '3', 'http://books.google.com/books/content?id=4WCNAW7r_5MC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Light and Color in the Outdoors')ON CONFLICT DO NOTHING;
